;    // freopen("output.txt", "w", stdout);    ll int t=1;    //cin>>t;    while(t--){        int n;        cin>>n;        int a[n];        for(int i=0;i<n;i++){            a[i]=i+1;        }        int s=(n*(n+1))/2;        if(s%2!=0){            cout<<"0";        }        else{            vector<vector<int>>dp(n,vector<int>(s+1,-1));            cout<<f(n-1,s/2,a,dp);        }    }        return 0;}