//SHREE GANESHAYA NAMAH//OM NAMAHA SHIVAY#include<bits/stdc++.h>#define ll long long#define endl '\n'#define pb push_back#define pob pop_back#define mp make_pair#define ff first#define ss second#define fastio() ios_base::sync_with_stdio(false); cin.tie(NULL); cout.tie(__null);using namespace std;// const int m=(int)1e9+7;// const int N=1000000;// etf in sqrt(n) using prime factorisation// ll int phi(ll int n){//     ll int res=n;//     for(ll int i=2;i*i<=n;i++){//         if(n%i==0){//             res/=i;//             res*=(i-1);//         }//         while(n%i==0){//             n/=i;//         }//     }//     if(n>1){//         res/=n;//         res*=(n-1);//     }//     return res;// }// etf in nlog(logn) using sieve algo// ll int p[1000001];// void phif(ll int n){//     for(ll int i=1;i<=n;i++){//         p[i]=i;//     }//     for(ll int i=2;i<=n;i++){//         if(p[i]==i){//             for(ll int j=i;j<=n;j+=i){//                 p[j]/=i;//                 p[j]*=(i-1);//             }//         }//     }//     // for(ll int i=0;i<n;i++){//     //     cout<<p[i]<<" ";//     // }// }// vector<ll int>factorial(N+20);// ll int bin(ll int a,ll int b,ll int m){//     ll int ans=1;//     while(b>0){//         if(b&1){//             ans=(ans*a)%m;//         }//         a=(a*a)%m;//         b>>=1;//     }//     return ans%m;// }// ll int cei(ll int a,ll int b){//     if(a%b==0){//         return a/b;//     }//     else{//         return (a/b)+1;//     }// }// bool prime(ll int n){//     for(ll int i=2;i*i<=n;i++){//         if(n%i==0){//             return false;//         }//     }//     return true;// }ll int f(ll int i,ll int p,ll int a[],ll int b[],vector<vector<ll int>>&dp){    if(i==0){        if(a[i]<=p){            return b[i];        }        return 0;    }    if(dp[i][p]!=-1){        return dp[i][p];    }    int t=-1e9,nt=f(i-1,p,a,b,dp);    if(a[i]<=p){        t=b[i]+f(i-1,p-a[i],a,b,dp);    }    dp[i][p]=max(t,nt);    return dp[i][p];}int main(){    fastio()    // freopen("input.txt", "r", stdin);    // freopen("output.txt", "w", stdout);    ll int t=1;    //cin>>t;    while(t--){        int n,x;        cin>>n>>x;        int h[n],s[n];        for(int i=0;i<n;i++){            cin>>h[i];        }        for(int i=0;i<n;i++){            cin>>s[i];        }        vector<vector<int>>dp(n,vector<int>(x+1,0));        for(int t=h[0];t<=x;t++){            dp[0][t]=s[0];        }        for(int i=1;i<n;i++){            for(int t=0;t<=x;t++){                int p=-1e9,np=dp[i-1][t];                if(h[i]<=t){                    p=s[i]+dp[i-1][t-h[i]];                }                dp[i][t]=max(p,np);            }        }        cout<<dp[n-1][x];    }        return 0;}