//SHREE GANESHAYA NAMAH//OM NAMAHA SHIVAY#include<bits/stdc++.h>#define ll long long#define endl '\n'#define pb push_back#define pob pop_back#define mp make_pair#define ff first#define ss second#define fastio() ios_base::sync_with_stdio(false); cin.tie(NULL); cout.tie(__null);using namespace std;const int m=(int)1e9+7;// const int N=1000000;// etf in sqrt(n) using prime factorisation// ll int phi(ll int n){//     ll int res=n;//     for(ll int i=2;i*i<=n;i++){//         if(n%i==0){//             res/=i;//             res*=(i-1);//         }//         while(n%i==0){//             n/=i;//         }//     }//     if(n>1){//         res/=n;//         res*=(n-1);//     }//     return res;// }// etf in nlog(logn) using sieve algo// ll int p[1000001];// void phif(ll int n){//     for(ll int i=1;i<=n;i++){//         p[i]=i;//     }//     for(ll int i=2;i<=n;i++){//         if(p[i]==i){//             for(ll int j=i;j<=n;j+=i){//                 p[j]/=i;//                 p[j]*=(i-1);//             }//         }//     }//     // for(ll int i=0;i<n;i++){//     //     cout<<p[i]<<" ";//     // }// }// vector<ll int>factorial(N+20);// ll int bin(ll int a,ll int b,ll int m){//     ll int ans=1;//     while(b>0){//         if(b&1){//             ans=(ans*a)%m;//         }//         a=(a*a)%m;//         b>>=1;//     }//     return ans%m;// }// ll int cei(ll int a,ll int b){//     if(a%b==0){//         return a/b;//     }//     else{//         return (a/b)+1;//     }// }// bool prime(ll int n){//     for(ll int i=2;i*i<=n;i++){//         if(n%i==0){//             return false;//         }//     }//     return true;// }int f(int i,int j,int n,vector<vector<char>>&grid,vector<vector<int>>&dp){    if(i==0 && j==0){        return 1;    }    if(i<0 || j<0 || i>=n || j>=n){        return 0;    }    if(grid[i][j]=='*'){        return 0;    }    if(dp[i][j]!=-1){        return dp[i][j];    }    int up=f(i-1,j,n,grid,dp);    int left=f(i,j-1,n,grid,dp);    dp[i][j]=((up%m)+(left%m))%m;    return dp[i][j];}int main(){    fastio()    // freopen("input.txt", "r", stdin);    // freopen("output.txt", "w", stdout);    ll int t=1;    //cin>>t;    while(t--){        int n;        cin>>n;        vector<vector<char>>grid;        for(int i=0;i<n;i++){            vector<char>v1;            for(int j=0;j<n;j++){                char c;                cin>>c;                v1.pb(c);            }            grid.pb(v1);        }        vector<vector<int>>dp(n,vector<int>(n,-1));        if(grid[0][0]=='*'){            dp[0][0]=0;        }        else{            dp[0][0]=1;        }        // dp[0][0]=1;        for(int i=0;i<n;i++){            for(int j=0;j<n;j++){                if(i==0 && j==0){                    continue;                }                int up=0,left=0;                if(i>0 && grid[i-1][j]=='.'){                    up=dp[i-1][j];                }                if(j>0 && grid[i][j-1]=='.'){                    left=dp[i][j-1];                }                dp[i][j]=((up%m)+(left%m))%m;            }        }        // cout<<dp[n-1][n-1];        if(grid[n-1][n-1]=='*'){            cout<<"0";        }        else{            cout<<dp[n-1][n-1];        }    }    return 0;}